import React, {Component} from 'react';
import {inject, observer} from "mobx-react";
import {Redirect} from 'react-router-dom';
import CKEditor from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';

@inject('stores')
@observer
class PostNew extends Component {
    state = {
        title: '',
        content: '',
        userId: 1,
        goToMain: false
    }

    render() {
        if(this.state.goToMain)
            return <Redirect to='/' />;

        return (
            <div>
                <div>
                    제목: <input value={this.state.title} onChange={this.updateTitle}/>
                </div>
                <div>
                    내용:
                    <div>

                        <CKEditor editor={ClassicEditor}
                            data={this.state.content}/>
                    </div>
                </div>
                <div>
                    <button onClick={this.addNewPost}>확인</button>
                </div>
            </div>
        );
    }

    addNewPost = async () => {
        if(await this.props.stores.postStore.addNewPost(this.state)) {
            await this.props.stores.postStore.fetchItems();
            this.setState({
                              ...this.state,
                              goToMain: true
                          });
        }
    }

    updateTitle = event => {
        this.setState( {
            ...this.state,
            title: event.target.value
        });
    }

    updateContent1 = (event, editor) => {
        this.setState( {
                           ...this.state,
                           content: editor.getData()
                       });
    }

    updateContent = event => {
        this.setState( {
                           ...this.state,
                           content: event.target.value
                       });
    }



}

export default PostNew;

