import React, {Component} from 'react';

class PostNew extends Component {
    state = {
        title: ',',
        content: ',',
        userId: 1
    }

    render() {
        return (
            <div>
                <div>
                    제목: <input value={this.state.title} onChange={this.updateTitle}/>
                </div>
                <div>
                    내용:
                    <div>
                        <textarea value={this.state.textarea} onChange={this.updateContent}/>
                    </div>
                </div>
                <div>
                    <button onClick={this.addNewPost}>확인</button>
                </div>
            </div>
        );
    }

    addNewPost = () => {
        this.props.stores.postStore.addNewPost(this.state);
    }

    updateTitle = event => {
        this.setState( {
            ...this.state,
            title: event.target.value
        });
    }

    updateContent = event => {
        this.setState( {
                           ...this.state,
                           content: event.target.value
                       });
    }



}

export default PostNew;
