import React, {Component} from 'react';
import {inject, observer} from "mobx-react";
import BoardList from './BoardList';
import PostView from './PostView';
import PostNew from './PostNew';
import {Link} from 'react-router-dom';

@inject('stores')
@observer
class Board extends Component {

    componentDidMount() {
        this.props.stores.postStore.fetchItems();
        console.log(this.props);
    }

    render () {
        if(this.props.match && this.props.match.params.postid)
            return <PostView postid={this.props.match.params.postid}/>

        if(this.props.location && this.props.location.pathname === '/board/new')
            return <PostNew />;

        if(this.props.location && this.props.location.pathname === '/board/join')
            return <Join />

        let p = this.props.stores.postStore;

        return (
            <div className='home'>

                <div><button onClick={p.fetchItems}>fetch posts</button></div>

                <div className='board'>
                    <table >
                        <thead>
                        <tr>
                            <th scope="cols">제목</th>
                            <th scope="cols">내용</th>
                            <th scope="cols">작성일자</th>
                        </tr>
                        </thead>
                        <tbody>
                            {p.items && <BoardList items={p.items}/>}
                        </tbody>
                    </table>
                    <div>
                        <Link to="/board/new"><button>새 글쓰기</button></Link>
                    </div>
                </div>
            </div>
        );
    };
}

export default Board;

