import React, {Component} from 'react';
import {inject, observer} from "mobx-react";

@inject('stores')
@observer
class Board extends Component {

    componentDidMount() {
        this.props.stores.postStore.fetchItems();
    }

    render () {
        let p = this.props.stores.postStore;

        return (
            <div className='home'>

                <div><button onClick={p.fetchItems}>fetch posts</button></div>

                <div className='board'>
                    <table >
                        <thead>
                        <tr>
                            <th scope="cols">제목</th>
                            <th scope="cols">내용</th>
                            <th scope="cols">작성일자</th>
                        </tr>
                        </thead>
                        <tbody>
                        {p.items && p.items.map(item => {
                            let created = new Date(item.created);

                            return (
                                <tr key={item.id}>
                                    <th scope="row">{item.title}</th>
                                    <td>{item.content}</td>
                                    <td><div>{created.getFullYear()}
                                        {created.getMonth() + 1}
                                        {created.getDate()}</div></td>
                                </tr>
                            )
                        })}
                        </tbody>
                    </table>
                </div>
            </div>
        );
        };
}

export default Board;

