import React, {Component} from 'react';
import {Link} from 'react-router-dom'
import {inject, observer} from "mobx-react";

@inject('stores')
@observer
class PostView extends Component {

    componentDidMount() {
        this.props.stores.postStore.fetchItem(this.props.postid);
    }

    render() {
        let p = this.props.stores.postStore;
        if(p.viewItem === null)
            return <div/>;
        return (
            <div>
                <div>
                    제목 : {p.viewItem.title}
                </div>
                <div>
                    내용 : {p.viewItem.content}
                </div>
                <div>
                    작성시간 : {new Date(p.viewItem.created).toLocaleDateString()}
                </div>
                <div>
                    <Link to="/">목록</Link>
                    <button onClick={this.deletePost}>삭제</button>
                </div>
            </div>
        )
    }

    deletePost = () => {
        if(! window.confirm('삭제하시겠습니까?') === false)
            return;

        this.props.stores.postStore.deleteItem(this.props.postid);

    }
}

export default PostView;
