class App extends React.Component{
  state = {
    location: 0
  }

  render() {
    let {location} = this.state;

     return (

        <div>
          {location === 0 && <Home/>}
          {location === 1 && <Page1/> }
          {location === 2 && <Page2/> }
          {location === 3 && <Page3/> }
        </div>
    );
  }
}

export default App;
