import React, {Component} from 'react';
import {Link, Redirect} from 'react-router-dom'
import {inject, observer} from "mobx-react";

@inject('stores')
@observer
class PostView extends Component {

    state = {
        goToList: false
    };

    componentDidMount() {
        this.props.stores.postStore.fetchItem(this.props.postid);
    }

    render() {
        if(this.state.goToList === true)
            return <Redirect to='/'/>

        let p = this.props.stores.postStore;
        if(p.viewItem === null)
            return <div/>;
        return (
            <div>
                <div>
                    제목 : {p.viewItem.title}
                </div>
                <div>
                    내용 : {p.viewItem.content}
                </div>
                <div>
                    작성시간 : {new Date(p.viewItem.created).toLocaleDateString()}
                </div>
                <div>
                    <Link to="/">목록</Link>
                    <button onClick={this.deletePost}>삭제</button>
                </div>
            </div>
        )
    }

    deletePost = async () => {
        if(! window.confirm('삭제하시겠습니까?') === false)
            return;


        if(await this.props.stores.postStore.deleteItem(this.props.postStore.viewItem.id)) {
            await this.props.stores.postStore.fetchItems();
            this.setState({
                goToList: true
            });
        }

    }
}

export default PostView;
